import React, { Component } from "react";
import Sim1 from "./sketch1";
import P5Wrapper from "./P5Wrapper";

class Simulations extends Component {
  state = {
    start: false,
    reset: false
  };

  render() {
    return (
      <div className="row">
        <div className="col-9">
          <P5Wrapper sketch={Sim1} p5Props={this.state} />
        </div>

        <div className="col-3">
          <table className="table table-bordered ml-2">
            <thead className="thead-dark">
              <th>Select</th>
              <th>Values</th>
            </thead>
            <tbody>
              <tr>
                <td>1</td>
                <td>2</td>
              </tr>
              <tr>
                <td>3</td>
                <td>4</td>
              </tr>
              <tr>
                <td>5</td>
                <td>6</td>
              </tr>
              <tr>
                <td>
                  <form>
                    <button
                      className="btn btn-dark m-2"
                      type="button"
                      name="start"
                      value={this.state.start}
                      onClick={this.ButtonClick}
                    >
                      Start
                    </button>
                  </form>
                </td>{" "}
                <td>
                  <button
                    className="btn btn-dark m-2"
                    type="button"
                    name="reset"
                    value={this.state.reset}
                    onClick={this.ButtonClick}
                  >
                    Reset
                  </button>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    );
  }
  ButtonClick = event => {
    const { value, name } = event.target;
    this.setState({ [name]: true });
  };
}

export default Simulations;
