import React from "react";
const SeasonConfig = {
  summer: {
    text: "Let us hit the beaches",
    iconName: "sun yellow",
    view:""
  },
  winter: {
      text:"Burrr it is cold",
      iconName:'snowflake blue',
      view:""
  }
};
const SeasonDisplay = ({ latitude }) => {
  const season = getSeason(latitude, new Date().getMonth());
  const {text,iconName:icon,view}=SeasonConfig[season];


  return (
    <React.Fragment>
      <h1 >
        <i className={`massive ${icon} icon`} />
      </h1>
      <div className={`${view} m-2 text-center display-3`}>{text}</div>
      <h1 >
        <i className={`massive ${icon} icon`} />
      </h1>
    </React.Fragment>
  );
};

export default SeasonDisplay;

const getSeason = (lat, month) => {
  if (month >= 3 && month <= 8) {
    return lat > 0 ? "summer" : "winter";
  } else {
    return lat > 0 ? "winter" : "summer";
  }
};
