import React from "react";
import Form from "./Form";
import Joi from "joi-browser";
import { getGenres } from "../services/fakeGenreService";
import { getMovie, saveMovie } from "../services/fakeMovieService";
class MovieForm extends Form {
  state = {
    data: { title: "", genre: "", NumberStock: "", rate: "" }, //lets for now forget the genre properties
    error: {},
    genres: []
  };
  schema = {
    title: Joi.string()
      .required()
      .label("Title"),
    NumberStock: Joi.number()
      .required()
      .min(0)
      .max(100),
    rate: Joi.number()
      .required()
      .min(0)
      .max(10),
    genre: Joi.string()
      .required()
      .label("Genre")
  };
  doSubmit = () => {
    // console.log("Submitted");
    saveMovie(this.state.data);
    this.prop.history.push("/movies");
  };
  componentDidMount() {
    const genres = getGenres();
    this.setState({ genres });

    const movieId = this.props.match.params.id;
    if (movieId === "new") return;

    const movie = getMovie(movieId);
    if (!movie) return this.props.history.replace("/not-found");

    this.setState({ data: this.changingDBtodta(movie) });
  }

  changingDBtodta = movie => {
    return {
      _id: movie._id,
      title: movie.title,
      genre: movie.genre._id,
      NumberStock: movie.numberInStock,
      rate: movie.dailyRentalRate
    };
  };

  render() {
    return (
      <React.Fragment>
        <br />
        <h1 className="backk">Movie Form</h1>
        <form onSubmit={this.handleSubmit}>
          {this.renderInput("title", "Title")}
          {this.renderSelect("genre", "Genre", this.state.genres)}
          {this.renderInput("NumberStock", "Number in Stock")}
          {this.renderInput("rate", "Rate")}
          {this.renderButton("Submit")}
        </form>
      </React.Fragment>
    );
  }
}

export default MovieForm;
