import React, { Component } from "react";
import p5 from "p5";

class Simulations extends Component {
  constructor(props) {
    super(props);
      this.myRef = React.createRef();
  }

  render() {
    //here will go the p5

    this.Sim1=new p5((p)=> {

        p.setup = () => {
            p.createCanvas(600, 400,).parent(this.myRef.current);

        };
        p.draw = () => {
            p.background(250,255,100);
        }
    });



    return (
      <div>
        <div ref={this.myRef}/>
      </div>
    );
  }
}

export default Simulations;
