import React from "react";
const SeasonConfig = {
  summer: {
    text: "Let us hits the beach",
    iconName: "sun"
  },
  winter: {
      text:"Burrr it is cold",
      iconName:'snowflake'
  }
};
const SeasonDisplay = ({ latitude }) => {
  const season = getSeason(latitude, new Date().getMonth());
const {text,iconName:icon}=SeasonConfig[season];


  return (
    <React.Fragment>
      <h1>
        <i className={`${icon} icon backgroundB`} />
      </h1>
      <div className="fontifyr">{text}</div>
      <h1>
        <i className={`${icon} icon`} />
      </h1>
    </React.Fragment>
  );
};

export default SeasonDisplay;

const getSeason = (lat, month) => {
  if (month >= 3 && month <= 8) {
    return lat > 0 ? "summer" : "winter";
  } else {
    return lat > 0 ? "winter" : "summer";
  }
};
