import React from "react";
import VideoItem from "./VideoItem";
import Spinner  from './Spinner'

const VideoList = ({videos,onVideoSelect,loading}) => {


  const List=videos.map(
      (vid,index)=>{
        return (
            <VideoItem
            key={index}
            video={vid}
            onVideoSelect={onVideoSelect}
        />)
      }
  );

   const bbb= ( loading)?<div className=" container ui relaxed divided list">{List}</div>:<Spinner/>;
    return bbb;
//please check for the spinner  for order to work every time or only the first time loading start
//it is mostly experimentall bullshit
    };

export default VideoList;
