import React, { Component } from "react";
import SeasonDisplay from "./Component/SeasonDisplay";

class App extends Component {
  state = {
    latitude: null,
    errorMessage: ""
  };
  componentDidMount() {
    window.navigator.geolocation.getCurrentPosition(
      position => {
        this.setState({ latitude: position.coords.latitude });
      },
      err => {
        this.setState({ errorMessage: err.message });
      }
    );
  }

  render() {
    const { latitude, errorMessage } = this.state;
    if (errorMessage && !latitude)
      return <div className="fontify alert-danger">{errorMessage}</div>;
    else if (latitude && !errorMessage)
      return (<SeasonDisplay latitude={this.state.latitude}/>);
    else return <div className="fontify">Loading</div>;
  }
}

export default App;
