import React, { Component } from "react";
import p5 from "p5";

class Simulations extends Component {
  constructor(props) {
    super(props);

  }

  render() {
    //here will go the p5
      this.myRef = React.createRef().bind(this);
    function Sim1(p) {

        p.setup = () => {
            p.createCanvas(600, 400,).parent(this.myRef.current);

        };
        p.draw= () => {
            p.background(0);
        }
    }

    let mySim1=new p5(Sim1);

    return (
      <div>
        <div ref={this.myRef}/>
      </div>
    );
  }
}

export default Simulations;
