import React, { Component } from "react";
import Form from "Form";
import Joi from "joi-browser";

class LoginForm extends Form {
  state = {
    data: { nameMovie: "", genre: "" },
    error: {}
  };
  // username = React.createRef(); //use it with the animation
  /* componentDidMount() {
      this.username.current.focus();
  }*/
  schema = {
    nameMovie: Joi.string()
      .required()
      .min(5)
      .label("Name"),
    genre: Joi.string()
      .required()
      .min(5)
      .label("Genre")
  };

  render() {
    const { data, error } = this.state;
    return (
      <div>
        <br />
        <h1 className="backk">Information</h1>
        <Form
        data={data}
        error={error}
        />
      </div>
    );
  }
}

export default LoginForm;
