import React, {Component} from 'react';
import p5 from "p5";
import PropTypes from 'prop-types'
import Sim1 from './sketch1'


class P5Wrapper extends Component {
    static propTypes = {
        p5Props: PropTypes.object.isRequired,
    };

    componentDidMount() {
        console.log(this.props.p5Props);
        this.canvas1.pro=this.props.p5Props;
         this.canvas1 =new p5(this.props.sketch,'parent');//this is the best way

         //adding the props

    }
    shouldComponentUpdate(nextProps) {
        this.canvas1.pro = nextProps.p5Props;
        return false
    }

    componentWillUnmount() {
        this.canvas1.remove();
    }

    render() {
        return (
            <>
                <div id="parent"/>
            </>
        );
    }

}

export default P5Wrapper;
